function send_ajax(a){if(a.type=="DELETE"){a.type="POST";a.data._method="DELETE"}else{if(a.type=="PUT"){a.type="POST";a.data._method="PUT"}}$.ajax({type:a.type,datatype:"json",data:a.data,contentType:a.contentType==null?"application/json;charset=utf-8":a.contentType,url:a.url,success:a.success,error:a.error})};